mapper.select:
SELECT COUNT(*)
  FROM dm_search_word
 WHERE dm_domain = #{dm_domain}
   AND dm_word = #{dm_word}

mapper.update:
UPDATE	dm_search_word 
   SET	dm_count = #{dm_count}
     ,  dm_sort = #{dm_sort}
     ,	dm_modify_dt = now()
 WHERE dm_id = #{dm_id}

UPDATE	dm_search_word 
   SET	dm_count = dm_count + 1
     ,	dm_modify_dt = now()
 WHERE dm_domain = #{dm_domain}
   AND dm_word = #{dm_word}

mapper.select:
SELECT	*
  FROM	dm_search_word
 WHERE dm_domain = #{dm_domain}
   AND dm_word = #{dm_word}

SELECT A.* , IFNULL((SELECT dm_domain_nm FROM dm_domain_list WHERE dm_id = A.dm_domain), '도메인정보없음') as dm_domain_text FROM dm_search_word A WHERE 1=1
ORDER BY dm_sort DESC, dm_count DESC
LIMIT #{page}, #{rows}
