mapper.insert:
INSERT INTO dm_crawl
	( dm_id
	, dm_href
	, dm_src
	, dm_type
	, dm_create_dt
	)
VALUES
	( #{dm_id}
	, #{dm_href}
	, #{dm_src}
	, #{dm_type}
	, now()
	)

mapper.select:
SELECT A.* , B.dm_code_name as dm_type FROM dm_crawl AS A INNER JOIN dm_common_code AS B ON A.dm_type = B.dm_code_value AND B.dm_code_group = '3004' WHERE 1=1
AND A.dm_status = #{dm_status}
ORDER BY A.dm_create_dt DESC, A.dm_id DESC LIMIT #{page}, #{rows}
