choose.otherwise:
AND (
	A.dm_vol = #{search_value}
	OR
	A.dm_title LIKE CONCAT('%',#{search_value},'%')
)

mapper.insert:
INSERT INTO dm_cover 
	(  dm_id
	 , dm_vol
	 , dm_year
	 , dm_month
	 , dm_title
	 , dm_content
	 , dm_status
	 , dm_create_dt
	 , dm_create_id
	)
VALUES
	(  #{dm_id} 
	 , #{dm_vol}
	 , #{dm_year}
	 , #{dm_month}
	 , #{dm_title}
	 , #{dm_content}
	 , #{dm_status}
	 , now()
	 , #{dm_create_id}
	)
ON DUPLICATE KEY UPDATE
	  dm_vol = #{dm_vol}
	, dm_year = #{dm_year}
	, dm_month = #{dm_month}
	, dm_title = #{dm_title}
	, dm_content = #{dm_content}
	, dm_status = #{dm_status}
	, dm_modify_dt = now()
	, dm_modify_id = #{dm_modify_id}

mapper.select:
SELECT * FROM dm_cover WHERE dm_delete_yn = 'N' AND dm_status = 1
AND dm_id != #{dm_id}
LIMIT 1

mapper.update:
UPDATE dm_cover
   SET dm_status = #{dm_status}
 WHERE dm_id = #{dm_id}

UPDATE  dm_cover
   SET  dm_delete_yn = 'Y'
      , dm_delete_dt = now()
      , dm_delete_id = #{dm_delete_id}
 WHERE  dm_id = #{dm_id}
